import java.util.Arrays;import java.util.Scanner;public class Anagram {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Enter first string: ");        String str1 = removeSpaces(sc.nextLine()).toLowerCase();        System.out.print("Enter second string: ");        String str2 = removeSpaces(sc.nextLine()).toLowerCase();        if (str1.length() != str2.length()) {            System.out.println("The strings are not anagrams.");            return;        }        char[] arr1 = str1.toCharArray();        char[] arr2 = str2.toCharArray();        Arrays.sort(arr1);        Arrays.sort(arr2);        if (Arrays.equals(arr1, arr2))            System.out.println("The strings are anagrams.");        else            System.out.println("The strings are not anagrams.");    }    public static String removeSpaces(String str) {        StringBuilder sb = new StringBuilder();        for (char c : str.toCharArray()) {            if (c != ' ') {                sb.append(c);            }        }        return sb.toString();    }}