import java.util.Scanner;public class LongestWord {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String sentence = sc.nextLine() + " ";        String word = "";        String longest = "";        for (int i = 0; i < sentence.length(); i++) {            char c = sentence.charAt(i);            if (c != ' ') {                word += c;            } else {                if (word.length() > longest.length()) {                    longest = word;                }                word = "";            }        }        System.out.println("Longest word: " + longest);    }}