import java.util.Scanner;public class MostOccurCharacter {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Enter a string: ");        String str = sc.nextLine().toLowerCase();        int[] freq = new int[26];        for (int i = 0; i < str.length(); i++) {            char c = str.charAt(i);            if (c >= 'a' && c <= 'z') {                freq[c - 'a']++;            }        }        int maxCount = 0;        char mostFrequent = ' ';        for (int i = 0; i < 26; i++) {            if (freq[i] > maxCount) {                maxCount = freq[i];                mostFrequent = (char) (i + 'a');            }        }        System.out.println("Most Frequent Character: '" + mostFrequent + "'");    }}