import java.util.Scanner;public class RemoveCharacter {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String str = sc.nextLine();        char removeChar = sc.next().charAt(0);        StringBuilder result = new StringBuilder();        for (char c : str.toCharArray()) {            if (c != removeChar) {                result.append(c);            }        }        System.out.println("Modified String: " + result.toString());    }}