import java.util.Scanner;public class Toggle {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String str = sc.nextLine();        StringBuilder toggled = new StringBuilder();        for (char c : str.toCharArray()) {            if (Character.isUpperCase(c)) {                toggled.append(Character.toLowerCase(c));            } else if (Character.isLowerCase(c)) {                toggled.append(Character.toUpperCase(c));            } else {                toggled.append(c);            }        }        System.out.println("Toggled string: " + toggled.toString());    }}